import { publicRequest, userRequest } from "../requestMethods";
import { getProductFailure, getProductSuccess ,getProductStart, deleteProductStart, deleteProductSuccess, deleteProductFailure} from "./productRedux";
import { loginFailure, loginStart,loginSucces, updateProductStart } from "./userRedux"

export const login = async (dispatch,user) => {
    dispatch(loginStart());
    try {
        const res = await publicRequest.post("/auth/login",user)
        dispatch(loginSucces(res.data));
    }catch(err){
        dispatch(loginFailure())
    }
};

export const getProducts = async (dispatch) => {
    dispatch(getProductStart());
    try {
        const res = await publicRequest.get("/products")
        dispatch(getProductSuccess(res.data));
    }catch(err){
        dispatch(getProductFailure())
    }
};

export const deleteProduct = async (id,dispatch) => {
    dispatch(deleteProductStart());
    try {
        const res = await userRequest.delete(`/products/${id}`)
        dispatch(deleteProductSuccess(id));
    }catch(err){
        dispatch(deleteProductFailure())
    }
};


export const updateProduct = async (id, user, dispatch) => {
    dispatch(updateProductStart());
    try {
        const res = await userRequest.delete(`/products/${id}`)
        dispatch(updateProductSuccess({id,user}));
    }catch(err){
        dispatch(updateProductFailure())
    }
};

export const addProduct = async (id,dispatch) => {
    dispatch(deleteProductStart());
    try {
        const res = await userRequest.delete(`/products/${id}`)
        dispatch(deleteProductSuccess(id));
    }catch(err){
        dispatch(deleteProductFailure())
    }
};
